namespace SMS
{
    public partial class ConvertFolderImageToBase64 : System.Web.UI.Page
    {
        SqlConnection con = new SqlConnection();
        protected void Page_Load(object sender, EventArgs e)
        {
            if (Request.QueryString["Name"].ToString() == "Staff")
            {
                btnAdd.Visible = false;
                btnStaff.Visible = true;
            }
            else if (Request.QueryString["Name"].ToString() == "Student")
            {
                btnAdd.Visible = true;
                btnStaff.Visible = false;
            }
            Connection();
        }
        protected void Connection()
        {
            try
            {
                string filePath1 = Server.MapPath(WebConfigurationManager.AppSettings["ConPath"].ToString());
                StreamReader sr = new StreamReader(filePath1);
                string line = sr.ReadToEnd();
                con = new SqlConnection(line);
                con.Open();
                con.Close();
            }
            catch (Exception ex)
            {
                LogFile.LogError(ex);
                Response.Redirect("SMS_SQL_Connection.aspx");
            }

        }

        protected void btnAdd_Click(object sender, EventArgs e)
        {
            //SqlCommand cmd1 = new SqlCommand("SP_Image_Save_Student_Folder_base64", con);
            //cmd1.CommandType = CommandType.StoredProcedure;
            //cmd1.Parameters.AddWithValue("@StudentId", "1");
            ////cmd1.Parameters.AddWithValue("@images", "");
            //SqlParameter imageParameter = new SqlParameter("@images", SqlDbType.Image);
            //imageParameter.Value = "";
            //cmd1.Parameters.Add(imageParameter);

            ////cmd1.Parameters.AddWithValue("@images", SqlDbType.Image).Value = "";

            //cmd1.Parameters.AddWithValue("@FilePath", "~/Images/StudentImages/.jpg");
            //con.Open();
            //cmd1.ExecuteNonQuery();
            //con.Close();

            SqlCommand cmd = new SqlCommand("SP_Insert_ConvertImageFoladerTobase64_Student", con);
            cmd.CommandType = CommandType.StoredProcedure;
            cmd.Connection = con;
            SqlDataAdapter da = new SqlDataAdapter(cmd);
            DataSet ds = new DataSet();
            da.Fill(ds);
            if (ds.Tables[0].Rows.Count > 0)
            {
                int count = 0;
                for (int i = 0; i < ds.Tables[0].Rows.Count; i++)
                {
                    try
                    {
                        System.Drawing.Image image = System.Drawing.Image.FromFile(Server.MapPath(ds.Tables[0].Rows[i]["StudentImages"].ToString()));
                        using (System.Drawing.Image thumnail = image.GetThumbnailImage(Convert.ToInt32(ConfigurationManager.AppSettings["Width"].ToString()), Convert.ToInt32(ConfigurationManager.AppSettings["Height"].ToString()), new System.Drawing.Image.GetThumbnailImageAbort(ThumbnailCallback), IntPtr.Zero))
                        {
                            using (MemoryStream ms = new MemoryStream())
                            {
                                count = count + 1;
                                thumnail.Save(ms, ImageFormat.Png);
                                byte[] bytes = new byte[ms.Length];
                                ms.Position = 0;
                                ms.Read(bytes, 0, (int)bytes.Length);
                                string base64string = Convert.ToBase64String(bytes, 0, bytes.Length);
                                string images = "data:image/png;base64," + base64string;
                                string FilePath = ds.Tables[0].Rows[i]["StudentImages"].ToString();
                                SqlCommand cmd1 = new SqlCommand("sp_image_save_student_folder_base64", con);
                                cmd1.CommandType = CommandType.StoredProcedure;
                                cmd1.Parameters.AddWithValue("@studentid", ds.Tables[0].Rows[i]["StudentID"].ToString());
                                cmd1.Parameters.AddWithValue("@Baseimages", base64string.ToString());
                                //cmd.Parameters.Add("@images", base64string);
                                cmd1.Parameters.AddWithValue("@filepath", FilePath.ToString());
                                con.Open();
                                cmd1.ExecuteNonQuery();
                                con.Close();
                            }
                        }
                    }
                    catch
                    { continue; }
                }
                divpopup.Attributes.Add("style", "display:block;");
                lblmsg.Text = "Count " + count + " Update Successfully!!";
            }
        }
        public bool ThumbnailCallback()
        {
            return false;
        }

        protected void btnStaff_Click(object sender, EventArgs e)
        {
            SqlCommand cmd = new SqlCommand("SP_Insert_ConvertImageFoladerTobase64_Staff", con);
            cmd.CommandType = CommandType.StoredProcedure;
            cmd.Connection = con;
            SqlDataAdapter da = new SqlDataAdapter(cmd);
            DataSet ds = new DataSet();
            da.Fill(ds);
            if (ds.Tables[0].Rows.Count > 0)
            {
                int count = 0;
                for (int i = 0; i < ds.Tables[0].Rows.Count; i++)
                {
                    try
                    {
                        System.Drawing.Image image = System.Drawing.Image.FromFile(Server.MapPath(ds.Tables[0].Rows[i]["Emp_Photo"].ToString()));
                        using (System.Drawing.Image thumnail = image.GetThumbnailImage(Convert.ToInt32(ConfigurationManager.AppSettings["Width"].ToString()), Convert.ToInt32(ConfigurationManager.AppSettings["Height"].ToString()), new System.Drawing.Image.GetThumbnailImageAbort(ThumbnailCallback), IntPtr.Zero))
                        {
                            using (MemoryStream ms = new MemoryStream())
                            {
                                count = count + 1;
                                thumnail.Save(ms, ImageFormat.Png);
                                byte[] bytes = new byte[ms.Length];
                                ms.Position = 0;
                                ms.Read(bytes, 0, (int)bytes.Length);
                                string base64string = Convert.ToBase64String(bytes, 0, bytes.Length);
                                string images = "data:image/png;base64," + base64string;
                                string FilePath = ds.Tables[0].Rows[i]["Emp_Photo"].ToString();
                                SqlCommand cmd1 = new SqlCommand("sp_image_save_staff_folder_base64", con);
                                cmd1.CommandType = CommandType.StoredProcedure;
                                cmd1.Parameters.AddWithValue("@StaffId", ds.Tables[0].Rows[i]["Staff_Id"].ToString());
                                cmd1.Parameters.AddWithValue("@base64images", base64string.ToString());
                                cmd1.Parameters.AddWithValue("@filepath", FilePath.ToString());
                                con.Open();
                                cmd1.ExecuteNonQuery();
                                con.Close();
                            }
                        }
                    }
                    catch
                    { continue; }
                }
                divpopup.Attributes.Add("style", "display:block;");
                lblmsg.Text = "Count "+ count + " Update Successfully!!";
            }
        }
        protected void lnkGo_Click(object sender, EventArgs e)
        {
            divpopup.Attributes.Add("style", "display:none;");
            //Response.Redirect("ConvertFolderImageToBase64.aspx");
        }
    }
